/* scroll to */
function scrollTo($target) {
    $('html, body').animate({ scrollTop: $target.offset().top }, 550);
}
function initTabs() {
    $('.tabs').each(function(i,e){
        var $tabs = $(this);
        $tabs.find('.tab-links a').on('click', function(e) {
            var $this = $(this);
            if (!$this.parent().hasClass("disabled")) {
                // show content tab
                var $thistab = $tabs.find($this.attr('href'));
                $this.parent('li').addClass('active').siblings().removeClass('active');
                $thistab.fadeIn(350).siblings().hide();

                // animate numbers
                $thistab.find('.counter').counterUp();
                // animate box
                var $time = 0; var $tpos = 0;
                $thistab.find('.box').each(function(ix,el){
                    var $tmp = $(this);
                    var $pos = $(this).offset().top;

                    if ($tpos != $pos) { $tpos = $pos; $time = $time + 150; }
                    $tmp.css({top: 20, opacity: 0}).stop().delay($time).animate({top: 0, opacity: 1}, 250);
                });
            }
            e.preventDefault();
        });
    });
}
function initProgress() {
    // progress bar
    $('.progress-bar').each(function(i,e){
        var $this = $(this);
        var $percent = $("<div class=\"percent\"></div>");
        var $value = $this.attr("data-value");
        $this.append($percent);

        $percent.css( {'background': $this.attr("data-color")});
        $percent.animate({width: $this.attr("data-percent") + '%'}, 350);

        if ($value != undefined && $value != "") {
            $divvalue = $("<span class=\"txt\">"+$value+"</span>");
            $this.append($divvalue);
            $divvalue.animate({'left': $this.attr("data-percent")+'%', opacity: 1});
        }
    });

    // progress pie
    $('.pie-chart').each(function(i,e){
        var $this = $(this);
        var $chart = $this.find(".canvas");
        var $opts = { responsive: true, title: { display: false }, legend: { display: false }, cutoutPercentage: 75 };
        var $ttext = []; var $tdata = []; var $tcolor = [];
        var $total = 0;

        $this.find("li").each(function($j, el){
            var $item = $(this);
            $ttext.push( $item.find("span").css({color: $item.attr("data-color")}).text() );
            $tdata.push( $item.attr("data-value") );
            $tcolor.push( $item.attr("data-color") );

            $item.find("span.square").css({background: $item.attr("data-color")});

            $total = $total + parseInt($item.attr("data-value"));
        });
        var $data = { labels: $ttext, datasets: [{ data: $tdata, backgroundColor: $tcolor, hoverBorderColor: "transparent" }]};

        new Chart($chart, { type: 'doughnut', data: $data, options: $opts });
    });
}

