function initFormFields() {

    // input
	$(".input input, .area textarea").each(function(i, e) {
		var $input = $(this);
		if ($input.val() == "") { $input.val($input.attr("data-holder")); }
		$input.focus(function() { $input.parent().addClass("focus"); if ($input.val() == $input.attr("data-holder")) { $input.val(""); } })
		$input.blur(function() { $input.parent().removeClass("focus"); if ($input.val() == "") { $input.val($input.attr("data-holder")); } })
	});
    // pulldown
    $(".pull select").each(function(i, e) {
		var $this = $(this);
        var $pull = $this.parent();

		if ($pull.find("div").length == 0) {
            $pull.prepend("<div>"+$this.children(":selected").text()+"</div>");
        }
		$this.change(function(){
			$pull.children("div").text($this.children(":selected").text());
		});

        if ($this.find('option').length <= 1) {
            $pull.addClass("disabled");
        }
    });
    // textarea
    $.each($('textarea[data-autoresize]'), function() {
        var offset = this.offsetHeight - this.clientHeight;

        var resizeTextarea = function(el) {
            $(el).css('height', 'auto').css('height', el.scrollHeight + offset);
        };
        $(this).on('keyup input', function() { resizeTextarea(this); }).removeAttr('data-autoresize');
    });
    // checkbox
    $('.check input').change(function(e){
        var $this = $(this);
        if ($this.is( ":checked" )) {
            $this.parent().addClass("checked");
        } else {
            $this.parent().removeClass("checked");
        }
    }).each(function(i,e){
        var $this = $(this);
        if ($this.is( ":checked" )) {
            $this.parent().addClass("checked");
        } else {
            $this.parent().removeClass("checked");
        }
    });
    // radio
    $('.radio input').change(function(e){
        var $this = $(this);
        var $name = $this.attr("name");

        $(".radio input:radio[name="+$name+"]").each(function(i,e){
            var $check = $(this);
            if ($check.is( ":checked" )) {
                $check.parent().addClass("checked");
            } else {
                $check.parent().removeClass("checked");
            }
        });
    }).each(function(i,e){
        var $this = $(this);
        if ($this.is( ":checked" )) {
            $this.parent().addClass("checked");
        } else {
            $this.parent().removeClass("checked");
        }
    });
}

/* scroll to */
function scrollTo($target) {
    $('html, body').animate({ scrollTop: $target.offset().top }, 550);
}
function initTabs() {
    $('.tabs').each(function(i,e){
        var $tabs = $(this);
        $tabs.find('.tab-links a').on('click', function(e) {
            var $this = $(this);
            if (!$this.parent().hasClass("disabled")) {
                // show content tab
                var $thistab = $tabs.find($this.attr('href'));
                $this.parent('li').addClass('active').siblings().removeClass('active');
                $thistab.fadeIn(350).siblings().hide();

                // animate numbers
                $thistab.find('.counter').counterUp();
                // animate box
                var $time = 0; var $tpos = 0;
                $thistab.find('.box').each(function(ix,el){
                    var $tmp = $(this);
                    var $pos = $(this).offset().top;

                    if ($tpos != $pos) { $tpos = $pos; $time = $time + 150; }
                    $tmp.css({top: 20, opacity: 0}).stop().delay($time).animate({top: 0, opacity: 1}, 250);
                });
            }
            e.preventDefault();
        });
    });
}
function initProgress() {
    // progress bar
    $('.progress-bar').each(function(i,e){
        var $this = $(this);
        var $percent = $("<div class=\"percent\"></div>");
        var $value = $this.attr("data-value");
        $this.append($percent);

        $percent.css( {'background': $this.attr("data-color")});
        $percent.animate({width: $this.attr("data-percent") + '%'}, 350);

        if ($value != undefined && $value != "") {
            $divvalue = $("<span class=\"txt\">"+$value+"</span>");
            $this.append($divvalue);
            $divvalue.animate({'left': $this.attr("data-percent")+'%', opacity: 1});
        }
    });

    // progress pie
    $('.pie-chart').each(function(i,e){
        var $this = $(this);
        var $chart = $this.find(".canvas");
        var $opts = { responsive: true, title: { display: false }, legend: { display: false }, cutoutPercentage: 75 };
        var $ttext = []; var $tdata = []; var $tcolor = [];
        var $total = 0;

        $this.find("li").each(function($j, el){
            var $item = $(this);
            $ttext.push( $item.find("span").css({color: $item.attr("data-color")}).text() );
            $tdata.push( $item.attr("data-value") );
            $tcolor.push( $item.attr("data-color") );

            $item.find("span.square").css({background: $item.attr("data-color")});

            $total = $total + parseInt($item.attr("data-value"));
        });
        var $data = { labels: $ttext, datasets: [{ data: $tdata, backgroundColor: $tcolor, hoverBorderColor: "transparent" }]};

        new Chart($chart, { type: 'doughnut', data: $data, options: $opts });
    });
}

